package hellojavaop;

public class Logicalop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int b = 15;
		int c = 25;
		
		System.out.println("a<b is " + (a<b));
		System.out.println("a>b is" +(a>b));
		System.out.println("a==b is" + (a==b));
		System.out.println("a!=b is" + (a!=b));
		System.out.println("a<b&&a>b is" + (a<b&&a>b));
		System.out.println("a>b||a>b" + (a>b||a<b));
	    
	}
}
   